mapper.select:
select
	count(1)
from
	user_action ua
where
	ua.target_seq = #{targetSeq}
	and ua.user_seq = #{userSeq}
	and ua.action_type = #{actionType}
	and ua.target_type = #{targetType}

select
	count(1)
from
	user_action
where
	target_seq = #{targetSeq}
	and target_type = #{targetType}
	and action_type = #{actionType}
